#include <node.h>
#include <stdlib.h>
#include "MathUtil.h"

MathUtil::MathUtil()
{
}

MathUtil::~MathUtil()
{
}

int MathUtil::GetA(int m, int n)
{
    int value = 1;
    for (int i = 0; i < n; i++) {
        value = value * (m - i);
    }
    return value;
}


int MathUtil::GetC(int m, int n)
{
    if ((m < n) || (n < 0)) {
        return 0;
    }
    return MathUtil::GetA(m, n) / MathUtil::GetA(n, n);
}