var AccountRole = function(){
    var self = this;
    self.info = [{id:'RS',code:'CHANNEL', name:'渠道用户'}];
    self.infoArray = {};
    self.init();
};

AccountRole.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

AccountRole.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new AccountRole();

