var GameGrade = function(){
    var self = this;
    self.info = [{id:1, code:'lv1', bonus:0, des:'组24'},
        {id:2, code:'lv2', bonus:0, des:'已中奖'},
        {id:3, code:'lv3', bonus:0, des:'未中奖'}];
    self.infoArray = {};
    self.init();
};

GameGrade.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    }
};

GameGrade.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new GameGrade();

