/**
 * 期次开奖
 * @param user
 * @param headNode
 * @param bodyNode
 * @param cb
 */
AdminControl.prototype.handleAD10 = function(user, headNode, bodyNode, cb)
{
    var backBodyNode = {};
    var table = dc.main.get("term");
    async.waterfall([
        //更新期次状态
        function(cb)
        {
            termSer.draw(bodyNode.termId, function(err, data){
                cb(err, data);
            });
        },
        //发送期次已经开奖的消息
        function(term, cb)
        {
            cb(null);
        }
    ], function (err, rst) {
        cb(err, backBodyNode);
    });
};


