var esut = require('easy_util');
var dc = require('mcp_db').dc;
var config = require('mcp_config');
var game = config.game;
var dateUtil = esut.dateUtil;
var digestUtil = esut.digestUtil;
var log = esut.log;
var pageUtil = esut.pageUtil;
var async = require('async');
var cons = require('mcp_constants');
var termStatus = cons.termStatus;
var msgStatus = cons.msgStatus;
var ticketPrintQueenStatus = cons.ticketPrintQueenStatus;

var MysqlPageControl = function(){};

MysqlPageControl.prototype.handle = function(headNode, bodyNode, cb)
{
    console.log(bodyNode);
    var self = this;
    var cmd = headNode.cmd;
    self[cmd[1]](headNode, bodyNode, cb);
};

MysqlPageControl.prototype.listTables = function(headNode, bodyNode, cb)
{
    var self = this;
    var backBodyNode = {title:""};
    pageUtil.parse(bodyNode, backBodyNode);
    backBodyNode.rst = dc.main.getAllTables();
    cb(null, backBodyNode);
};

MysqlPageControl.prototype.index = function(headNode, bodyNode, cb)
{
    var self = this;
    var backBodyNode = {title:""};
    cb(null, backBodyNode);
};

module.exports = new MysqlPageControl();