int Check::getHitCount(IntArray *lotArray, IntArray *prizeArray){
    IntArray *lot = lotArray;
    int len = lot->length();
    IntArray *prize = prizeArray;
    int plen = prize->length();
    int hitCount = 0; //中红球的个数
     for(int i = 0; i< len; i++){
            for (int j = 0; j < plen; j++){
                if (lot->get(i) == prize->get(j)){
                    hitCount ++;
                }
            }
        }
    return hitCount;
}


